import React, { Component } from 'react';
import "./Home.css"
import Products from "./Products"
import Navbar from "./Navbar"
import {connect} from 'react-redux';
import {filterData} from '../redux/selectors'

class Home extends Component{
    constructor(props){
        super(props);
        this.state = {data:[]}
    }
    componentDidMount(){
        if(Object.keys(this.props.filter).find(d=> this.props.filter[d].length))
        this.setState({data:[...this.props.data]})
        else
        filterData(this.props.data,this.props.filter)
    }
    render(){
        return(
        <div>
            <Navbar handleSearch={this.props.handleSearch}/>
            {this.state.data.length ? <Products data={this.state.data}/> : null}
        </div>
        )
    }
}

const mapStateToProps = state =>{
    const {data,filter} = state;
    return {data,filter};
}
export default connect(mapStateToProps,null)(Home);
