import { combineReducers } from 'redux';
import login from './login';
import register from './register';
import product from "./product";
const loginState = {
  loginLoading: false,
  loginUser: '',
  loginError: null
}

const registerState = {
  registerLoading: false,
  registerMsg: '',
  registerError: null
}

const dataState = {
  loading: false,
  data: [],
  error: null,
  filter:{brand:[],price:[]},
  filterData:[]
}


export default function combined(state,action) {
  return {
    login: login({...state.loginLoading,...state.loginUser,...state.loginError}, action),
    register: register({...state.registerLoading,...state.registerMsg,...state.registerError}, action),
    product:product(...state.loading,...state.data,...state.error,...state.filter,...state.filterData,action)
  }
}
