const initialState = {
    search:''
}

export default function(state = initialState,action){
    switch(action.type){
        case 'SEARCH_PRODUCT':{
            const {content} = action.payload;
            return{
                ...state,
                search:content
            }
        }
        default:{
            return state;
        }
    }
}
