import {createStore,applyMiddleware,compose} from 'redux';
import reducer from "./reducers"
import thunk from "redux-thunk";

const loginState = {
    loginLoading: false,
    loginUser: '',
    loginError: null
}

const registerState = {
    registerLoading: false,
    registerMsg: '',
    registerError: null
}

const dataState = {
    loading: false,
    data: [],
    error: null,
    filter:{brand:[],price:[]},
    filterData:[]
}


export default createStore(reducer,{...loginState,registerState,dataState},compose(applyMiddleware(thunk),window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()));
