const initialState = {
    loading: false,
    data: [],
    error: null,
    filter:{brand:[],price:[]},
}

export default function(state = initialState,action){
    switch(action.type){
        case 'SEARCH_PRODUCT':
      return {
        ...state,
        loading: true
      };
    case 'SEARCH_PRODUCT_SUCCESS':
      return {
        ...state,
        loading: false,
        error: null,
        data: [...state.data, ...action.payload.data]
      };
    case 'SEARCH_PRODUCT_FAILURE':
      return {
        ...state,
        loading: false,
        error: action.payload.error
      };
    case 'FILTER_DATA':
      if(action.payload.checked)
      return {
        ...state,
        filter:{...state.filter,[action.payload.name]:[...state.filter[action.payload.content],action.payload.id]}
      }
      else{
        var idx = state.filter[action.payload.name].indexOf(action.payload.id)
        state.filter[action.payload.name].splice(idx,1)
      return {
        filter:{...state.filter,[action.payload.name]:[...state.filter[action.payload.content],action.payload.id]}
      }
    }
    default:
      return state;
    }
}
