import React, { Component } from 'react';
import "./Products.css";
import Container from "react-bootstrap/Container"
import Form from 'react-bootstrap/Form'

class Products extends Component{
    constructor(props){
        super(props);
        this.state = {clear:'',brand:{},max:''};
        this.handleRemoveHover = this.handleRemoveHover.bind(this)
    }
    handleHover(evt,i){
        let x = evt.target.parentNode.children[0];
        let idx = 0;
        let clear = setInterval(()=>{
            if(idx > this.props.data[i].media.standard.length-1)
            idx = 0;
            x.src = this.props.data[i].media.standard[idx].url
            idx++;
        },1000);
        this.setState({clear:clear})
    }
    handleRemoveHover(){
        clearInterval(this.state.clear)
    }
    componentDidMount(){
        let x = this.props.data.map(d=> d.brand.name);
        let mySet = new Set([...x]);
        let y = this.props.data.map(m=>m.price.offer_price.value);
        let val = y.reduce((accumulator,currentValue,idx)=> {
            if(idx === 0){
            accumulator = currentValue;
            return accumulator        
            }
            else
            return Math.max(accumulator,currentValue)
        })
        this.setState({max:val})
        this.setState({brand:mySet});
    }
    render(){
        return(
            <div style={{position:'relative',top:'15vh',left:'0',zIndex:'99'}}>
                <div className='filter'>
                    <h4 className='pt-2 pl-2'>Filters</h4>
                    <div className='p-3 gender-filter'>
                    <Form>
                        <div key={`default-checkbox`} className="mb-3">
                          <Form.Check 
                            custom
                            type='checkbox'
                            id={`  Men`}
                            label={`  Men`}
                          />
                          <Form.Check
                            custom 
                            type='checkbox'
                            id={`  Women`}
                            label={`  Women`}
                          />
                          <Form.Check
                            custom 
                            type='checkbox'
                            id={`  Boys`}
                            label={`  Boys`}
                          />
                          <Form.Check
                            custom 
                            type='checkbox'
                            id={`  Girls`}
                            label={`  Girls`}
                          />
                        </div>
                    </Form>
                    </div>
                    <h4 className='pt-2 pl-2'>Brand</h4>
                    <div className='p-3 gender-filter'>
                    <Form>
                        <div key={`default-checkbox`} className="mb-3">
                            {Array.from(this.state.brand).map(m=>(
                                <Form.Check
                                custom
                                type='checkbox'
                                id={`  ${m}`}
                                label={`  ${m}`}
                                />
                            ))}
                        </div>
                    </Form>
                    </div>
                </div>
                <div className='main'>
                    <div className='grid-container'>
                        {this.props.data.map((d,i)=> (
                        <div className={`grid-item grid-item-${i}`} onMouseOver={(evt)=>this.handleHover(evt,i)} onMouseOut={this.handleRemoveHover}>
                            <img className='tiles' src={d.media.standard[0].url}/>
                        <div className='tiles-info p-3' style={{lineHeight:'10px'}}>
                        <span className='cart mb-3'>Add to cart</span>
                                <p><strong>{d.brand.name.length > 20 ? `${d.brand.name.substring(0,20).toUpperCase()}..` : d.brand.name}</strong></p>
                                <p style={{fontSize:'14px'}}>{d.name.substring(0,28)}...</p>
                        <p><strong>RS. {d.price.offer_price.value} </strong>
                        {d.discount === 0 ? null : <><span style={{textDecoration:'line-through',fontSize:'12px'}}>RS. {d.price.basket_price.value}</span><span style={{color:'#FEA774',fontSize:'12px'}}>({d.discount.toFixed(1)}% OFF)</span></>}
                        </p>
                            </div>
                        </div>
                        ))}
                    </div>
                    </div>
            </div>
        )
    }
}

export default Products;
