import React, { Component } from 'react';
import "./Products.css";
import Container from "react-bootstrap/Container"
import anime from 'animejs/lib/anime.es.js';


class Products extends Component{
    constructor(props){
        super(props);
        this.state = {}
        this.myRef = React.createRef();
        this.handleImage = this.handleImage.bind(this);
    }
    handleImage(evt,i){
        // let idx = 0;
        // let x = evt.target.parentNode.children[0];
        // anime({
        //     targets:x,
        //     width:'0%',
        //     easing:'linear',
        //     duration:200,
        //     begin:function(){
        //         x.src = `${this.props.data[i].media.standard[idx+1].url}`
        //         anime({
        //             targets:x,
        //             width:'100%',
        //             easing:'linear',
        //             duration:200
        //         })
        //     }
        // })
    }
    render(){
        return(
            <div>
                <div className='filter'></div>
                <div className='main'>
                    <div className='grid-container'>
                        {this.props.data.map((d,i)=> (
                        <div className={`grid-item grid-item-${i}`} onMouseEnter={(evt)=>this.handleImage(evt,i)}>
                            <img className='tiles' src={d.media.standard[0].url} ref={this.myRef}/>
                            <div className='tiles-info p-3' style={{lineHeight:'10px',position:'absolute',top:'80%'}}>
                                <p><strong>{d.brand.name.length > 20 ? `${d.brand.name.substring(0,20).toUpperCase()}..` : d.brand.name}</strong></p>
                                <p style={{fontSize:'14px'}}>{d.name.substring(0,28)}...</p>
                        <p><strong>RS. {d.price.offer_price.value} </strong>
                        {d.discount === 0 ? null : <><span style={{textDecoration:'line-through',fontSize:'12px'}}>RS. {d.price.basket_price.value}</span><span style={{color:'#FEA774',fontSize:'12px'}}>({d.discount}% OFF)</span></>}
                        </p>
                            </div>
                        </div>
                        ))}
                    </div>
                    </div>
                </div>
        )
    }
}

export default Products;
