const initialState = {
    loading: false,
    todos: [],
    error: null
}

export default function(state = initialState,action){
    switch(action.type){
        case 'SEARCH_PRODUCT':
      return {
        ...state,
        loading: true
      };
    case 'SEARCH_PRODUCT_SUCCESS':
      return {
        ...state,
        loading: false,
        error: null,
        todos: [...state.todos, action.payload]
      };
    case ADD_TODO_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload.error
      };
    default:
      return state;
    }
}
