import React, { Component } from 'react';
import "./Products.css";
import Container from "react-bootstrap/Container"

class Products extends Component{
    constructor(props){
        super(props);
        this.state = {}
    }
    render(){
        return(
            <div>
                <div className='filter'></div>
                <div className='main'>

                    <div className='grid-container'>
                        {this.props.data.map((d,i)=> (
                        <div className={`grid-item grid-item-${i}`}>
                            <img className='tiles' src={d.media.standard[0].url} />
                            <div className='tiles-info p-3' style={{lineHeight:'10px'}}>
                                <p><strong>{d.brand.name.toUpperCase()}</strong></p>
                                <p style={{fontSize:'14px'}}>{d.name.substring(0,30)}...</p>
                                <p><strong>RS. {d.price.offer_price.value} </strong><span style={{textDecoration:'line-through',fontSize:'12px'}}>RS. {d.price.basket_price.value}</span></p>
                            </div>
                        </div>
                        ))}
                        {/* <div className='col-lg-2 tiles'></div>
                        <div className='col-lg-2 tiles'></div>
                        <div className='col-lg-2 tiles'></div>
                        <div className='col-lg-2 tiles'></div> */}
                    </div>
                    </div>
                </div>
        )
    }
}

export default Products;
