import React, { Component } from 'react';
import Form from 'react-bootstrap/Form'
import "./Filter.css"

class Filter extends Component{
    constructor(props){
        super(props)
        this.state = {}
    }
    render(){
        return(
            <div className='filter'>
                <h4 className='p-2 '>Filters</h4>
                    <div className='p-2 gender-filter'>
                    <Form>
                    <div key={`default-checkbox`} className="mb-3">
                        {['Men','Women','Boys','Girls'].map(e=>(
                          <Form.Check 
                          custom
                          type='checkbox'
                          id={e}
                          label={e}
                        />  
                        ))}
                        </div>
                        {/* <div key={`default-checkbox`} className="mb-3">
                          <Form.Check 
                            custom
                            type='checkbox'
                            id={`Men`}
                            label={`  Men`}
                          />
                          <Form.Check
                            custom 
                            type='checkbox'
                            id={`Women`}
                            label={`  Women`}
                          />
                          <Form.Check
                            custom 
                            type='checkbox'
                            id={`Boys`}
                            label={`  Boys`}
                          />
                          <Form.Check
                            custom 
                            type='checkbox'
                            id={`  Girls`}
                            label={`  Girls`}
                          />
                        </div> */}
                    </Form>
                    </div>

                    <div className='p-2 gender-filter'>
                    <h4 className='py-1'>Brand</h4>
                    <Form>
                        <div key={`default-checkbox`} className="mb-3">
                            {Array.from(this.props.brand).map(m=>(
                                <Form.Check
                                custom
                                type='checkbox'
                                id={`  ${m}`}
                                label={`  ${m}`}
                                />
                            ))}
                        </div>
                    </Form>
                    </div>

                    <div className='p-2 gender-filter price-filter'>
                    <h4 className='py-1'>Price</h4>
                    <Form>
                        <div key={`default-checkbox`} className="mb-3">
                                <Form.Check
                                custom
                                type='checkbox'
                                id={`  ${this.props.max / 3 }`}
                                label={`  Rs. 0 to Rs. ${this.props.max / 3 }`}
                                />

                                <Form.Check
                                custom
                                type='checkbox'
                                id={`  ${this.props.max / 3 * 2}`}
                                label={`  Rs. ${this.props.max} to Rs.${this.props.max / 3 * 2}`}
                                />

                                <Form.Check
                                custom
                                type='checkbox'
                                id={`  ${this.props.max}`}
                                label={`  Rs.${this.props.max /3 *2} to Rs. ${this.props.max}`}
                                />
                        </div>
                    </Form>
                    </div>
                </div>
        )
    }
}

export default Filter;
