const initialState = {
    loading: false,
    data: [],
    error: null,
    filter:{brand:[],price:[]},
}

export default function(state = initialState,action){
    switch(action.type){
        case 'SEARCH_PRODUCT':
      return {
        ...state,
        loading: true
      };
    case 'SEARCH_PRODUCT_SUCCESS':
      return {
        ...state,
        loading: false,
        error: null,
        data: [...state.data, ...action.payload.data]
      };
    case 'SEARCH_PRODUCT_FAILURE':
      return {
        ...state,
        loading: false,
        error: action.payload.error
      };
    case 'FILTER_DATA':
      if(action.payload.checked)
      return {
        ...state,
        filter:{...state.filter,[action.payload.name]:[...state.filter[action.payload.name],action.payload.id]}
      }
      else if(!action.payload.checked){
         let idx = state.filter[action.payload.name].indexOf(action.payload.id)
         let arr1 = [...state.filter[action.payload.name]]
        let arr = [...state.filter[action.payload.name].splice(idx,1)]
        console.log(arr)
      return {
        ...state,
        filter:{...state.filter,[action.payload.name]:[...arr]}
      }
    }
    default:
      return state;
    }
}
